import React from 'react';
import { TaskView } from './views/task-view';

export const App: React.FC = () => {
  return (
    <TaskView />
  );
};

export type SyncStatus = 'synced' | 'syncing' | 'error' | 'pending';

export interface ITask {
    id: string;
    title: string;
    description: string;
    completed: boolean;
    syncStatus?: SyncStatus;
    lastSyncError?: string;
  }

  export class TaskModel implements ITask {
    private _id: string;          
    private _title: string;
    private _description: string;       
    private _completed: boolean;
    private _syncStatus: SyncStatus;
    private _lastSyncError?: string;

    constructor(
      id: string, 
      title: string, 
      description: string = '', 
      completed: boolean = false,
      syncStatus: SyncStatus = 'pending',
      lastSyncError?: string
    ) {
      this._id = id;
      this._title = title;
      this._description = description;
      this._completed = completed;
      this._syncStatus = syncStatus;
      this._lastSyncError = lastSyncError;
    }

    get id(): string { return this._id; }
    get title(): string { return this._title; }
    get description(): string { return this._description; }
    get completed(): boolean { return this._completed; }
    get syncStatus(): SyncStatus { return this._syncStatus; }
    get lastSyncError(): string | undefined { return this._lastSyncError; }

    toggle(): TaskModel {
      return new TaskModel(
        this._id, 
        this._title, 
        this._description, 
        !this._completed, 
        'pending'
      );
    }

    updateTitle(title: string): TaskModel {
      return new TaskModel(
        this._id,
        title,
        this._description,
        this._completed,
        'pending'
      );
    }

    updateDescription(description: string): TaskModel {
      return new TaskModel(
        this._id,
        this._title,
        description,
        this._completed,
        'pending'
      );
    }

    withSyncStatus(syncStatus: SyncStatus, error?: string): TaskModel {
      return new TaskModel(
        this._id,
        this._title,
        this._description,
        this._completed,
        syncStatus,
        error
      );
    }
  }

import { TaskModel } from '../models/task';

export interface ICommand {
  execute(): void;
  undo(): void;
  getDescription(): string;
}

export interface TaskStoreState {
  tasks: Map<string, TaskModel>;
  nextId: number;
}

export abstract class TaskCommand implements ICommand {
  protected store: TaskStoreState;

  constructor(store: TaskStoreState) {
    this.store = store;
  }

  abstract execute(): void;
  abstract undo(): void;
  abstract getDescription(): string;
}

export class AddTaskCommand extends TaskCommand {
  private task: TaskModel;

  constructor(store: TaskStoreState, title: string, description: string = '') {
    super(store);
    this.task = new TaskModel(
      `task_${store.nextId}`,
      title,
      description,
      false,
      'pending'
    );
  }

  execute(): void {
    this.store.tasks.set(this.task.id, this.task);
    this.store.nextId++;
  }

  undo(): void {
    this.store.tasks.delete(this.task.id);
    this.store.nextId--;
  }

  getDescription(): string {
    return `Add task: ${this.task.title}`;
  }

  getTaskId(): string {
    return this.task.id;
  }
}

export class DeleteTaskCommand extends TaskCommand {
  private taskId: string;
  private deletedTask: TaskModel | null = null;

  constructor(store: TaskStoreState, taskId: string) {
    super(store);
    this.taskId = taskId;
  }

  execute(): void {
    this.deletedTask = this.store.tasks.get(this.taskId) || null;
    this.store.tasks.delete(this.taskId);
  }

  undo(): void {
    if (this.deletedTask) {
      this.store.tasks.set(this.taskId, this.deletedTask);
    }
  }

  getDescription(): string {
    return `Delete task: ${this.deletedTask?.title || this.taskId}`;
  }
}

export class ToggleTaskCommand extends TaskCommand {
  private taskId: string;
  private originalTask: TaskModel | null = null;

  constructor(store: TaskStoreState, taskId: string) {
    super(store);
    this.taskId = taskId;
  }

  execute(): void {
    this.originalTask = this.store.tasks.get(this.taskId) || null;
    if (this.originalTask) {
      const toggledTask = this.originalTask.toggle();
      this.store.tasks.set(this.taskId, toggledTask);
    }
  }

  undo(): void {
    if (this.originalTask) {
      this.store.tasks.set(this.taskId, this.originalTask);
    }
  }

  getDescription(): string {
    const action = this.originalTask?.completed ? 'Mark incomplete' : 'Mark complete';
    return `${action}: ${this.originalTask?.title || this.taskId}`;
  }
}

export class UpdateTaskTitleCommand extends TaskCommand {
  private taskId: string;
  private newTitle: string;
  private originalTask: TaskModel | null = null;

  constructor(store: TaskStoreState, taskId: string, newTitle: string) {
    super(store);
    this.taskId = taskId;
    this.newTitle = newTitle;
  }

  execute(): void {
    this.originalTask = this.store.tasks.get(this.taskId) || null;
    if (this.originalTask) {
      const updatedTask = this.originalTask.updateTitle(this.newTitle);
      this.store.tasks.set(this.taskId, updatedTask);
    }
  }

  undo(): void {
    if (this.originalTask) {
      this.store.tasks.set(this.taskId, this.originalTask);
    }
  }

  getDescription(): string {
    return `Update title: ${this.originalTask?.title || this.taskId} â†’ ${this.newTitle}`;
  }
}

export class UpdateTaskDescriptionCommand extends TaskCommand {
  private taskId: string;
  private newDescription: string;
  private originalTask: TaskModel | null = null;

  constructor(store: TaskStoreState, taskId: string, newDescription: string) {
    super(store);
    this.taskId = taskId;
    this.newDescription = newDescription;
  }

  execute(): void {
    this.originalTask = this.store.tasks.get(this.taskId) || null;
    if (this.originalTask) {
      const updatedTask = this.originalTask.updateDescription(this.newDescription);
      this.store.tasks.set(this.taskId, updatedTask);
    }
  }

  undo(): void {
    if (this.originalTask) {
      this.store.tasks.set(this.taskId, this.originalTask);
    }
  }

  getDescription(): string {
    return `Update description: ${this.originalTask?.title || this.taskId}`;
  }
}

import { TaskModel, ITask, SyncStatus } from '../models/task';
import { 
  ICommand, 
  TaskStoreState, 
  AddTaskCommand, 
  DeleteTaskCommand, 
  ToggleTaskCommand, 
  UpdateTaskTitleCommand, 
  UpdateTaskDescriptionCommand 
} from './commands';

const API_BASE_URL = 'https://api.example.com/tasks';
const AUTO_SAVE_DEBOUNCE_MS = 1000; // 1 second debounce
const MAX_HISTORY_SIZE = 10; // Maximum number of operations to keep for undo

export class TaskStore implements TaskStoreState {
  private _tasks: Map<string, TaskModel> = new Map();     
  private _listeners: Set<() => void> = new Set();       
  private _nextId: number = 1;
  private _autoSaveTimeouts: Map<string, NodeJS.Timeout> = new Map();

  // Undo/Redo functionality
  private _history: ICommand[] = [];
  private _redoStack: ICommand[] = [];

  // Expose state for commands
  get tasks(): Map<string, TaskModel> { return this._tasks; }
  get nextId(): number { return this._nextId; }
  set nextId(value: number) { this._nextId = value; }

  private _logTaskOperation(action: string, taskId: string): void {
    const timestamp = new Date().toISOString();
    console.log(`[${timestamp}] Action: ${action} Task: ${taskId}`);
  }

  // Command execution with history management
  private _executeCommand(command: ICommand): void {
    command.execute();

    // Add to history and maintain max size
    this._history.push(command);
    if (this._history.length > MAX_HISTORY_SIZE) {
      this._history.shift(); // Remove oldest command
    }

    // Clear redo stack when new command is executed
    this._redoStack = [];

    this._notifyListeners();
  }

  // Undo/Redo methods
  canUndo(): boolean {
    return this._history.length > 0;
  }

  canRedo(): boolean {
    return this._redoStack.length > 0;
  }

  undo(): boolean {
    if (!this.canUndo()) return false;

    const command = this._history.pop()!;
    command.undo();
    this._redoStack.push(command);

    // Maintain max redo stack size
    if (this._redoStack.length > MAX_HISTORY_SIZE) {
      this._redoStack.shift();
    }

    this._notifyListeners();
    return true;
  }

  redo(): boolean {
    if (!this.canRedo()) return false;

    const command = this._redoStack.pop()!;
    command.execute();
    this._history.push(command);

    this._notifyListeners();
    return true;
  }

  getLastOperation(): string | null {
    if (this._history.length === 0) return null;
    return this._history[this._history.length - 1].getDescription();
  }

  getNextRedoOperation(): string | null {
    if (this._redoStack.length === 0) return null;
    return this._redoStack[this._redoStack.length - 1].getDescription();
  }

  // API sync methods
  private async _syncTaskToAPI(task: TaskModel): Promise<void> {
    try {
      const response = await fetch(API_BASE_URL, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          id: task.id,
          title: task.title,
          description: task.description,
          completed: task.completed,
        }),
      });

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      // Update sync status to synced
      const syncedTask = task.withSyncStatus('synced');
      this._tasks.set(task.id, syncedTask);
      this._logTaskOperation('synced', task.id);
      this._notifyListeners();
    } catch (error) {
      console.error('Sync failed:', error);
      // Update sync status to error
      const errorTask = task.withSyncStatus('error', error instanceof Error ? error.message : 'Sync failed');
      this._tasks.set(task.id, errorTask);
      this._logTaskOperation('sync_error', task.id);
      this._notifyListeners();
    }
  }

  private _scheduleAutoSave(taskId: string): void {
    // Clear existing timeout for this task
    const existingTimeout = this._autoSaveTimeouts.get(taskId);
    if (existingTimeout) {
      clearTimeout(existingTimeout);
    }

    // Schedule new auto-save
    const timeout = setTimeout(() => {
      const task = this._tasks.get(taskId);
      if (task && task.syncStatus === 'pending') {
        this._autoSaveTask(taskId);
      }
      this._autoSaveTimeouts.delete(taskId);
    }, AUTO_SAVE_DEBOUNCE_MS);

    this._autoSaveTimeouts.set(taskId, timeout);
  }

  private async _autoSaveTask(taskId: string): Promise<void> {
    const task = this._tasks.get(taskId);
    if (!task) return;

    // Set syncing status
    const syncingTask = task.withSyncStatus('syncing');
    this._tasks.set(taskId, syncingTask);
    this._logTaskOperation('auto_sync_start', taskId);
    this._notifyListeners();

    await this._syncTaskToAPI(syncingTask);
  }

  addTask(title: string, description: string = ''): ITask {
    const command = new AddTaskCommand(this, title, description);
    this._executeCommand(command);

    const taskId = (command as AddTaskCommand).getTaskId();
    this._logTaskOperation('create', taskId);

    // Schedule auto-save for new task
    this._scheduleAutoSave(taskId);

    return this._tasks.get(taskId)!; 
  }

  updateTaskTitle(id: string, title: string): void {
    const task = this._tasks.get(id);
    if (task && task.title !== title) {
      const command = new UpdateTaskTitleCommand(this, id, title);
      this._executeCommand(command);
      this._logTaskOperation('update_title', id);

      // Schedule auto-save
      this._scheduleAutoSave(id);
    }
  }

  updateTaskDescription(id: string, description: string): void {
    const task = this._tasks.get(id);
    if (task && task.description !== description) {
      const command = new UpdateTaskDescriptionCommand(this, id, description);
      this._executeCommand(command);
      this._logTaskOperation('update_description', id);

      // Schedule auto-save
      this._scheduleAutoSave(id);
    }
  }

  toggleTask(id: string): void {
    const task = this._tasks.get(id);
    if (task) {
      const command = new ToggleTaskCommand(this, id);
      this._executeCommand(command);
      this._logTaskOperation('toggle', id);

      // Schedule auto-save
      this._scheduleAutoSave(id);
    }
  }

  deleteTask(id: string): void {
    if (this._tasks.has(id)) {
      // Clear any pending auto-save
      const existingTimeout = this._autoSaveTimeouts.get(id);
      if (existingTimeout) {
        clearTimeout(existingTimeout);
        this._autoSaveTimeouts.delete(id);
      }

      const command = new DeleteTaskCommand(this, id);
      this._executeCommand(command);
      this._logTaskOperation('delete', id);
    }
  }

  // Manual sync for retry functionality
  async retrySyncTask(id: string): Promise<void> {
    const task = this._tasks.get(id);
    if (task && task.syncStatus === 'error') {
      await this._autoSaveTask(id);
    }
  }

  getTasks(): ITask[] {
    return Array.from(this._tasks.values()).map(task => ({
      id: task.id,
      title: task.title,
      description: task.description,
      completed: task.completed,
      syncStatus: task.syncStatus,
      lastSyncError: task.lastSyncError
    }));
  }

  subscribe(listener: () => void): () => void {
    this._listeners.add(listener);
    return () => this._listeners.delete(listener);
  }

  private _generateId(): string {
    return `task_${this._nextId++}`;
  }

  private _notifyListeners(): void {
    this._listeners.forEach(listener => listener());
  }

  // Cleanup method for component unmount
  cleanup(): void {
    this._autoSaveTimeouts.forEach(timeout => clearTimeout(timeout));
    this._autoSaveTimeouts.clear();
  }
}

export const taskStore = new TaskStore();

import React, { useState, useEffect } from 'react';
import { taskStore } from '../stores/task-store';
import { SyncStatus } from '../models/task';

interface EditingState {
  [taskId: string]: {
    title?: boolean;
    description?: boolean;
  };
}

export const TaskView: React.FC = () => {
  const [tasks, setTasks] = useState(taskStore.getTasks());
  const [newTaskTitle, setNewTaskTitle] = useState('');
  const [newTaskDescription, setNewTaskDescription] = useState('');
  const [editing, setEditing] = useState<EditingState>({});
  const [canUndo, setCanUndo] = useState(taskStore.canUndo());
  const [canRedo, setCanRedo] = useState(taskStore.canRedo());
  const [lastOperation, setLastOperation] = useState(taskStore.getLastOperation());
  const [nextRedoOperation, setNextRedoOperation] = useState(taskStore.getNextRedoOperation());

  const _isValidTitle = (title: string): boolean => {
    return title.trim().length > 0 && title.trim().length < 100;
  };

  useEffect(() => {
    const unsubscribe = taskStore.subscribe(() => {
      setTasks(taskStore.getTasks());
      setCanUndo(taskStore.canUndo());
      setCanRedo(taskStore.canRedo());
      setLastOperation(taskStore.getLastOperation());
      setNextRedoOperation(taskStore.getNextRedoOperation());
    });

    // Cleanup on unmount
    return () => {
      unsubscribe();
      taskStore.cleanup();
    };
  }, []);

  // Separate useEffect for keyboard shortcuts
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if ((e.metaKey || e.ctrlKey) && e.key === 'z' && !e.shiftKey) {
        e.preventDefault();
        if (taskStore.canUndo()) {
          taskStore.undo();
        }
      } else if ((e.metaKey || e.ctrlKey) && (e.key === 'y' || (e.key === 'z' && e.shiftKey))) {
        e.preventDefault();
        if (taskStore.canRedo()) {
          taskStore.redo();
        }
      }
    };

    document.addEventListener('keydown', handleKeyDown);

    return () => {
      document.removeEventListener('keydown', handleKeyDown);
    };
  }, []);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (_isValidTitle(newTaskTitle)) {
      taskStore.addTask(newTaskTitle.trim(), newTaskDescription.trim());
      setNewTaskTitle('');
      setNewTaskDescription('');
    }
  };

  const handleToggle = (id: string) => {
    taskStore.toggleTask(id);
  };

  const handleDelete = (id: string) => {
    taskStore.deleteTask(id);
  };

  const handleTitleEdit = (id: string, newTitle: string) => {
    if (_isValidTitle(newTitle)) {
      taskStore.updateTaskTitle(id, newTitle);
    }
  };

  const handleDescriptionEdit = (id: string, newDescription: string) => {
    taskStore.updateTaskDescription(id, newDescription);
  };

  const handleRetrySync = (id: string) => {
    taskStore.retrySyncTask(id);
  };

  const handleUndo = () => {
    taskStore.undo();
  };

  const handleRedo = () => {
    taskStore.redo();
  };

  const startEditing = (taskId: string, field: 'title' | 'description') => {
    setEditing(prev => ({
      ...prev,
      [taskId]: {
        ...prev[taskId],
        [field]: true
      }
    }));
  };

  const stopEditing = (taskId: string, field: 'title' | 'description') => {
    setEditing(prev => ({
      ...prev,
      [taskId]: {
        ...prev[taskId],
        [field]: false
      }
    }));
  };

  const getSyncStatusIcon = (syncStatus?: SyncStatus): string => {
    switch (syncStatus) {
      case 'synced': return 'âœ…';
      case 'syncing': return 'ðŸ”„';
      case 'error': return 'âŒ';
      case 'pending': return 'â³';
      default: return 'â³';
    }
  };

  const getSyncStatusColor = (syncStatus?: SyncStatus): string => {
    switch (syncStatus) {
      case 'synced': return '#28a745';
      case 'syncing': return '#007bff';
      case 'error': return '#dc3545';
      case 'pending': return '#ffc107';
      default: return '#ffc107';
    }
  };

  return (
    <div className="task-view">
      <h1>Tasks</h1>

      {/* Undo/Redo Controls */}
      <div style={{ 
        marginBottom: '20px', 
        padding: '10px', 
        backgroundColor: '#f8f9fa', 
        borderRadius: '5px',
        display: 'flex',
        gap: '10px',
        alignItems: 'center'
      }}>
        <button 
          onClick={handleUndo}
          disabled={!canUndo}
          style={{
            backgroundColor: canUndo ? '#007bff' : '#6c757d',
            color: 'white',
            border: 'none',
            borderRadius: '3px',
            padding: '6px 12px',
            fontSize: '14px',
            cursor: canUndo ? 'pointer' : 'not-allowed'
          }}
          title={lastOperation ? `Undo: ${lastOperation}` : 'Nothing to undo'}
        >
          â†¶ Undo
        </button>

        <button 
          onClick={handleRedo}
          disabled={!canRedo}
          style={{
            backgroundColor: canRedo ? '#28a745' : '#6c757d',
            color: 'white',
            border: 'none',
            borderRadius: '3px',
            padding: '6px 12px',
            fontSize: '14px',
            cursor: canRedo ? 'pointer' : 'not-allowed'
          }}
          title={nextRedoOperation ? `Redo: ${nextRedoOperation}` : 'Nothing to redo'}
        >
          â†· Redo
        </button>

        {lastOperation && (
          <span style={{ fontSize: '12px', color: '#6c757d' }}>
            Last: {lastOperation}
          </span>
        )}
      </div>

      <form onSubmit={handleSubmit}>
        <div>
          <input
            type="text"
            value={newTaskTitle}
            onChange={(e) => setNewTaskTitle(e.target.value)}
            placeholder="Add a new task..."
            required
          />
        </div>
        <div>
          <textarea
            value={newTaskDescription}
            onChange={(e) => setNewTaskDescription(e.target.value)}
            placeholder="Task description (optional)..."
            rows={3}
          />
        </div>
        <button type="submit">Add</button>
      </form>

      <ul>
        {tasks.map(task => (
          <li key={task.id} style={{ 
            marginBottom: '15px', 
            padding: '10px', 
            border: '1px solid #ddd', 
            borderRadius: '5px',
            borderLeft: `4px solid ${getSyncStatusColor(task.syncStatus)}`
          }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
              <label style={{ flex: 1 }}>
                <input
                  type="checkbox"
                  checked={task.completed}
                  onChange={() => handleToggle(task.id)}
                />

                {editing[task.id]?.title ? (
                  <input
                    type="text"
                    defaultValue={task.title}
                    style={{
                      marginLeft: '8px',
                      fontWeight: 'bold',
                      border: '1px solid #ddd',
                      borderRadius: '3px',
                      padding: '2px 4px'
                    }}
                    onBlur={(e) => {
                      handleTitleEdit(task.id, e.target.value);
                      stopEditing(task.id, 'title');
                    }}
                    onKeyDown={(e) => {
                      if (e.key === 'Enter') {
                        handleTitleEdit(task.id, e.currentTarget.value);
                        stopEditing(task.id, 'title');
                      } else if (e.key === 'Escape') {
                        stopEditing(task.id, 'title');
                      }
                    }}
                    autoFocus
                  />
                ) : (
                  <span 
                    style={{
                      textDecoration: task.completed ? 'line-through' : 'none',
                      fontWeight: 'bold',
                      marginLeft: '8px',
                      cursor: 'pointer'
                    }}
                    onClick={() => startEditing(task.id, 'title')}
                    title="Click to edit"
                  >
                    {task.title}
                  </span>
                )}
              </label>

              <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                <span 
                  style={{ 
                    fontSize: '16px',
                    cursor: task.syncStatus === 'error' ? 'pointer' : 'default'
                  }}
                  title={task.syncStatus === 'error' ? `Error: ${task.lastSyncError}. Click to retry.` : `Status: ${task.syncStatus}`}
                  onClick={() => task.syncStatus === 'error' && handleRetrySync(task.id)}
                >
                  {getSyncStatusIcon(task.syncStatus)}
                </span>

                <button 
                  onClick={() => handleDelete(task.id)}
                  style={{
                    backgroundColor: '#ff4444',
                    color: 'white',
                    border: 'none',
                    borderRadius: '3px',
                    padding: '4px 8px',
                    fontSize: '12px',
                    cursor: 'pointer'
                  }}
                >
                  Delete
                </button>
              </div>
            </div>

            {editing[task.id]?.description ? (
              <textarea
                defaultValue={task.description}
                style={{
                  marginTop: '5px',
                  marginLeft: '24px',
                  width: 'calc(100% - 24px)',
                  minHeight: '60px',
                  border: '1px solid #ddd',
                  borderRadius: '3px',
                  padding: '4px'
                }}
                onBlur={(e) => {
                  handleDescriptionEdit(task.id, e.target.value);
                  stopEditing(task.id, 'description');
                }}
                onKeyDown={(e) => {
                  if (e.key === 'Escape') {
                    stopEditing(task.id, 'description');
                  } else if (e.key === 'Enter' && e.ctrlKey) {
                    handleDescriptionEdit(task.id, e.currentTarget.value);
                    stopEditing(task.id, 'description');
                  }
                }}
                autoFocus
              />
            ) : (
              task.description && (
                <div 
                  style={{
                    marginTop: '5px',
                    marginLeft: '24px',
                    color: '#666',
                    fontSize: '14px',
                    textDecoration: task.completed ? 'line-through' : 'none',
                    cursor: 'pointer',
                    padding: '2px 4px',
                    borderRadius: '3px',
                    backgroundColor: '#f8f9fa'
                  }}
                  onClick={() => startEditing(task.id, 'description')}
                  title="Click to edit"
                >
                  {task.description}
                </div>
              )
            )}

            {!task.description && !editing[task.id]?.description && (
              <div 
                style={{
                  marginTop: '5px',
                  marginLeft: '24px',
                  color: '#999',
                  fontSize: '14px',
                  fontStyle: 'italic',
                  cursor: 'pointer',
                  padding: '2px 4px'
                }}
                onClick={() => startEditing(task.id, 'description')}
                title="Click to add description"
              >
                + Add description
              </div>
            )}

            {task.syncStatus === 'error' && (
              <div style={{
                marginTop: '5px',
                marginLeft: '24px',
                color: '#dc3545',
                fontSize: '12px',
                fontStyle: 'italic'
              }}>
                Sync failed: {task.lastSyncError}
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};
