import { useState, useEffect, useCallback } from 'react';

export function useDebouncedSearch(
  initialValue: string = '',
  delay: number = 300
): [string, string, (value: string) => void] {
  const [inputValue, setInputValue] = useState(initialValue);
  const [debouncedValue, setDebouncedValue] = useState(initialValue);

  useEffect(() => {
    const timer = setTimeout(() => {
      setDebouncedValue(inputValue);
    }, delay);

    return () => {
      clearTimeout(timer);
    };
  }, [inputValue, delay]);

  const handleChange = useCallback((value: string) => {
    setInputValue(value);
  }, []);

  return [inputValue, debouncedValue, handleChange];
}

export type SyncStatus = 'synced' | 'syncing' | 'error' | 'pending';

export interface ITask {
    id: string;
    title: string;
    description: string;
    completed: boolean;
    syncStatus?: SyncStatus;
    lastSyncError?: string;
  }

  export class TaskModel implements ITask {
    private _id: string;          
    private _title: string;
    private _description: string;       
    private _completed: boolean;
    private _syncStatus: SyncStatus;
    private _lastSyncError?: string;

    constructor(
      id: string, 
      title: string, 
      description: string = '', 
      completed: boolean = false,
      syncStatus: SyncStatus = 'pending',
      lastSyncError?: string
    ) {
      this._id = id;
      this._title = title;
      this._description = description;
      this._completed = completed;
      this._syncStatus = syncStatus;
      this._lastSyncError = lastSyncError;
    }

    get id(): string { return this._id; }
    get title(): string { return this._title; }
    get description(): string { return this._description; }
    get completed(): boolean { return this._completed; }
    get syncStatus(): SyncStatus { return this._syncStatus; }
    get lastSyncError(): string | undefined { return this._lastSyncError; }

    toggle(): TaskModel {
      return new TaskModel(
        this._id, 
        this._title, 
        this._description, 
        !this._completed, 
        'pending'
      );
    }

    updateTitle(title: string): TaskModel {
      return new TaskModel(
        this._id,
        title,
        this._description,
        this._completed,
        'pending'
      );
    }

    updateDescription(description: string): TaskModel {
      return new TaskModel(
        this._id,
        this._title,
        description,
        this._completed,
        'pending'
      );
    }

    withSyncStatus(syncStatus: SyncStatus, error?: string): TaskModel {
      return new TaskModel(
        this._id,
        this._title,
        this._description,
        this._completed,
        syncStatus,
        error
      );
    }
  }

import { TaskModel } from '../models/task';

export interface ICommand {
  execute(): void;
  undo(): void;
  getDescription(): string;
}

export interface TaskStoreState {
  tasks: Map<string, TaskModel>;
  nextId: number;
}

export abstract class TaskCommand implements ICommand {
  protected store: TaskStoreState;

  constructor(store: TaskStoreState) {
    this.store = store;
  }

  abstract execute(): void;
  abstract undo(): void;
  abstract getDescription(): string;
}

export class AddTaskCommand extends TaskCommand {
  private task: TaskModel;

  constructor(store: TaskStoreState, title: string, description: string = '') {
    super(store);
    this.task = new TaskModel(
      `task_${store.nextId}`,
      title,
      description,
      false,
      'pending'
    );
  }

  execute(): void {
    this.store.tasks.set(this.task.id, this.task);
    this.store.nextId++;
  }

  undo(): void {
    this.store.tasks.delete(this.task.id);
    this.store.nextId--;
  }

  getDescription(): string {
    return `Add task: ${this.task.title}`;
  }

  getTaskId(): string {
    return this.task.id;
  }
}

export class DeleteTaskCommand extends TaskCommand {
  private taskId: string;
  private deletedTask: TaskModel | null = null;

  constructor(store: TaskStoreState, taskId: string) {
    super(store);
    this.taskId = taskId;
  }

  execute(): void {
    this.deletedTask = this.store.tasks.get(this.taskId) || null;
    this.store.tasks.delete(this.taskId);
  }

  undo(): void {
    if (this.deletedTask) {
      this.store.tasks.set(this.taskId, this.deletedTask);
    }
  }

  getDescription(): string {
    return `Delete task: ${this.deletedTask?.title || this.taskId}`;
  }
}

export class ToggleTaskCommand extends TaskCommand {
  private taskId: string;
  private originalTask: TaskModel | null = null;

  constructor(store: TaskStoreState, taskId: string) {
    super(store);
    this.taskId = taskId;
  }

  execute(): void {
    this.originalTask = this.store.tasks.get(this.taskId) || null;
    if (this.originalTask) {
      const toggledTask = this.originalTask.toggle();
      this.store.tasks.set(this.taskId, toggledTask);
    }
  }

  undo(): void {
